script:
frappe.listview_settings['Job'] = {
    refresh: function(listview) {
        listview.page.add_inner_button(__('Match Payments'), function() {
            frappe.msgprint("The Process Has been Started")
            frappe.call({
                method: 'agarwals.reconciliation.job.run',
                args: {'job_config': 'reconciliation_cron'},
                callback:function(r){
                 frappe.msgprint("The Process Has been Completed")
                }
            });
        }).addClass('btn-primary');
    }
};
